import "bootstrap/dist/css/bootstrap.min.css";
import 'bootstrap/dist/js/bootstrap.bundle.min.js'
import Routes from "./Routes";
import { useEffect, useState } from "react";
import { getAll } from "./api/call-api";

function App() {
  const [products, setProducts] = useState([]);
      useEffect(() => {
        console.log("useeffect");
        try {
        getAll().then(response => {
          setProducts(response.data);
          
        })
        } catch (error) {
          console.log("failed: ", error)
        }
      }, []);
const [user, setUser] = useState("");
 useEffect(() => {
   console.log("useeffect");
   try {
     // call dữ liệu từ api => chuyển dữ liệu sang duôi ".json" => truyền dữ liệu vào user
     //  fetch('https://6156c5bce039a0001725abbf.mockapi.io/users')
     // .then(response => response.json())
     // .then(data => setUsers(data));
     //cal api bằng axios
     getAll().then(response => setUsers(response.data));
   } catch (error) {
     console.log(error);
   }
 }, []); 
// delete product
const onHandleDelete = (id) =>{
  const resultDelete = products.filter(product => product.id !== id);
 setProducts(resultDelete)
}
const onHandleEdit =(product) =>{
  const productsUpdate = products.map(item =>
    item.id === product.id ? product : item
  );
setProducts(productsUpdate);
}
const onHandleAdd = (product) =>{
  setProducts([...products, product]);
}
  return (
    <div className="App">
      <Routes products = {products}
                  onDelete = {onHandleDelete}
                  onEdit = {onHandleEdit}
                  onAdd = {onHandleAdd}
      />
    </div>
  );
}

export default App;
