import { Link } from 'react-router-dom';
import '../styles/signin.css';
import { useForm } from "react-hook-form";
import { ToastContainer, toast } from "react-toastify";
import { signin } from '../api/auth';
function Signin() {
  const {register, handleSubmit, formState : {errors}} = useForm();
  const onHandleSubmit = (data) =>{
      try {
        const result  = signin(data);
        console.log(result)
      } catch (error) {
        toast.error(error.response.data);
      }
  }
    return (
      <div className="body-signin">
        <main className="form-signin">
          <form onSubmit={handleSubmit(onHandleSubmit)}>
            <img
              className="mb-4"
              src="https://lh3.googleusercontent.com/proxy/jyIjOEtoh32ZvAp8zyuHqiC409Jw8_wGD-lIkMbMbeB3eHFpiS1kKIER4aoxzEtL_AuuK61H4OMw9IB7JeCjmeNZm_68hOC-iDqPHPPgxQ"
              alt=""
              width={72}
              height={57}
            />
            <h1 className="h3 mb-3 fw-normal">Please sign in</h1>
            <div className="mb-3">
              <label htmlFor="floatingInput">Email address</label>
              <input
                type="email"
                className='form-control'
                placeholder="name@example.com"
                {...register("username", { required: true })}
              />
            </div>
            <div className="mb-3">
              <label htmlFor="floatingPassword">Password</label>
              <input
                type="password"
                className="form-control"
                placeholder="Password"
                {...register("password", { required: true })}
              />
            </div>
            <div className="checkbox mb-3">
              <label>
                <input type="checkbox" defaultValue="remember-me" /> Remember me
              </label>
            </div>
            <Link
              to="/signup"
              className="w-40 btn btn-lg btn-success"
              type="submit">
              Sign up
            </Link>
            <button className="w-40 btn btn-lg btn-primary" type="submit">
              Sign in
            </button>
          </form>
        </main>
      </div>
    );
}

export default Signin;
